import React from 'react';
import axios from "axios"
import "./MyPage.scss"
import Book from "../../assets/images/book.jpg"
import EventStructure from "../../components/EventStructure/EventStructure"


const APIURL = "http://localhost:8080/events"
class MyPage extends React.Component {

  state = {
    eventList: {},
    sugestedEvent: []


  }

  
  componentDidMount() {
  
    axios.get(APIURL)
      .then(res => {
        console.log(res.data);
        this.setState({ sugestedEvent: res.data })
      })
      .then(res => {
        const tag = this.props.tag ? this.props.tag : this.state.sugestedEvent[0].tag
        axios.get(APIURL + "/" + tag)
          .then(res => {
            this.setState({ eventList: res.data })
          })

      })
      .catch(err => {
        console.error(err)
      })
  }
  componentDidUpdate = () => {
    window.scrollTo(0, 0)
    if (this.props.tag !== undefined && this.state.eventList.tag !== this.props.tag) {
      console.log(this.props.tag)
      console.log(this.state.eventList.tag !== this.props.tag)
      axios.get(APIURL + "/" + this.props.tag)
        .then((res => {
          this.setState({ eventList: res.data })
        }))
    } if (this.props.tag === undefined && this.state.eventList.tag !== this.state.sugestedEvent[0].tag) {
      axios.get(APIURL + "/" + this.state.sugestedEvent[0].tag)
        .then((res => {
          this.setState({ eventList: res.data })
        }),

        )
        .catch(err => {
          console.error(err)
        })
    }
  }
  
  handleAttend
  render() {

    return (
      <div className="myPage">
        <div className="myPage__heading">
          <h2>EVENTS TO ATTEND</h2>
        </div>
        <div className="myPage__events">
          <EventStructure
            event={this.event}
            date={this.date}
            organizer={this.organizer}
            tags={this.tags}
            description={this.description}
          />
        </div>
        <div className="myPage__heading">
          <h2>SUGGESTIONS</h2>
        </div>
        <div className="myPage__suggestions">
          {
            this.state.sugestedEvent
              .filter(event => event.id !== this.state.sugestedEvent.id)
              .map((event) =>
                <EventStructure
                  event={event.event}
                  date={event.date}
                  organizer={event.organizer}
                  tags={event.tags}
                  description={event.description}
                />
              )
          }
        </div>
      </div>
    )
  }

}

export default MyPage
