import React from 'react'
import "./CreateEvent.scss"
import CreateInput from '../../components/CreateInput/CreateInput'
import { Link } from 'react-router-dom'
import CancelButton from '../../components/CancelButton/CancelButton'
import SubmitButton from '../../components/SubmitButton/SubmitButton'

class CreateEvent extends React.Component {

  state = {
    event: "",
    img: "",
    description: "",
    tags: [],

  }

  handleChange = (event) => {
    event.preventDefault();

  };

  render() {
    return (
      <>
        <div className="heading">
          <h2>CREATE AN EVENT</h2>
        </div>
        <div className="create">
          <form className="create__form">
            <div className="create__event-and-name">
              <label className="create__event-label">Event name</label>
              <input className="create__event"
                name="title"
                type="text"
                placeholder="event name"
                value={this.state.event} onChange={(e) => {
                  this.setState({ event: e.target.value })
                }} />
              <label className="create__event"></label>
              <input className="create__date">

              </input>
            </div>
          </form>
          <form>

          </form>
        </div>
      </>
    )
  }

}

export default CreateEvent
