import React from 'react';
import axios from "axios"
import "./MyPage.scss"

import EventStructure from "../../components/EventStructure/EventStructure"


const APIURL = "http://localhost:8080/events"
class MyPage extends React.Component {

  state = {
    eventList: [],
    suggestedEvent: [],
    attending: [],
    notAttending: [],
    currentEvent: {}
  }

  
  componentDidMount() {
  
    axios.get(APIURL)
      .then(res => {
        console.log(res.data);
        let attending = []
        let notAttending = []
        res.data.forEach((event) => {
          if (event.attend) {
            attending.push(event)
          } else {
            notAttending.push(event)
          }
        })
        this.setState({ attending, notAttending })
      })
      /* .then(res => {
        const tag = this.props.tag ? this.props.tag : this.state.suggestedEvent[0].tag
        axios.get(APIURL + "/" + tag)
          .then(res => {
            this.setState({ eventList: res.data })
          })
          
      }) */
      .catch(err => {
        console.error(err)
      })
      
  }
  componentDidUpdate = () => {
    /* window.scrollTo(0, 0)
    if (this.props.tag !== undefined && this.state.eventList.tag !== this.props.tag) {
      console.log(this.props.tag)
      console.log(this.state.eventList.tag !== this.props.tag)
      axios.get(APIURL + "/" + this.props.tag)
        .then((res => {
          this.setState({ eventList: res.data })
        }))
    } if (this.props.tag === undefined && this.state.eventList.tag !== this.state.suggestedEvent[0].tag) {
      axios.get(APIURL + "/" + this.state.suggestedEvent[0].tag)
        .then((res => {
          this.setState({ eventList: res.data })
        }),

        )
        .catch(err => {
          console.error(err)
        })
    } */
  }
  
  /* handleAttend = (event) =>  {
    event.preventDefault()
    this.setState ({attend: true})
  } */

  handleAttend = (e, event) => {
    e.preventDefault();
    let newNotAttendingList = this.state.notAttending.filter((notAttendingEvent) => {
      return notAttendingEvent.id !== event.id
    })
    let newAttendingList = [ ...this.state.attending ]
    newAttendingList.push(event)
    this.setState({
      notAttending: newNotAttendingList,
      attending: newAttendingList,
    })
  }

  render() {

    return (
      <div className="myPage">
        <div className="myPage__heading">
          <h2>EVENTS TO ATTEND</h2>
        </div>
        <div className="myPage__events">
          {
            this.state.attending
              .filter(event => event.id !== this.state.suggestedEvent.id)
              .map((event) =>
                <EventStructure
                  key={event.id}
                  event={event.event}
                  date={event.date}
                  organizer={event.organizer}
                  tags={event.tags}
                  description={event.description}
                  button={event.attend}
                  attendButtonClick={(e) => this.handleNotAttend(e, event)}
                  
                />
              )
          }
        </div>
        <div className="myPage__heading">
          <h2>SUGGESTIONS</h2>
        </div>
        <div className="myPage__suggestions">
          {
            this.state.notAttending
              .filter(event => event.id !== this.state.suggestedEvent.id)
              .map((event) =>
                <EventStructure
                  key={event.id}
                  event={event.event}
                  date={event.date}
                  organizer={event.organizer}
                  tags={event.tags}
                  description={event.description}
                  button={event.attend}
                  attendButtonClick={(e) => this.handleAttend(e, event)}
                  
                />
              )
          }
        </div>
      </div>
    )
  }

}

export default MyPage
