import axios from 'axios'
import React from 'react'
import "./MyInterests.scss"

const APIURL = "http://localhost:8080/interests"

class MyInterests extends React.Component {
  state = {
    id: "",
    name: "",
    activity1: "",
    activity2: "",
    activity3: "",
    activity4: "",
    activity5: "",
  }

  handleInterests = (e) => {
    e.preventDefault();
    axios.post(`${APIURL}`, {
      name: e.target.name.value,
      activities: e.target.interest.value,

    })
    console.log(e.target)
      .then(res => {
        console.log(e.target)
        console.log(res.data);
        this.props.history.push('/profile')
        alert('thank you!')
      })
      .catch(err => {
        console.error(err)
      })

    console.log(e.target)
  }

  handleActivitiesUpdate = (e) => {
    console.log(e.target.name)
    const inputControl = {
      'activity1': () => this.setState({ activity1: e.target.value }),
      'activity2': () => this.setState({ activity2: e.target.value }),
      'activity3': () => this.setState({ activity3: e.target.value }),
      'activity4': () => this.setState({ activity4: e.target.value }),
      'activity5': () => this.setState({ activity5: e.target.value }),
    };
    inputControl[e.target.name]()
  }



  render() {
    console.log(this.state)
    return (
      <>
        <div className="interests">
          <form className="interests__form"
            onSubmit={(e) => {
              this.handleInterests(e)
            }}>
            <h1 className="interests__title">Chose 5 activities that describes you the most:</h1>
            <p>1 - <input
              className="create__event"
              name="interest1"
              type="text"
              placeholder="What are you looking for?"
              value={this.state.activities} onChange={(e) => this.handleActivitiesUpdate(e)}
            />
            </p>
            <p>2 - <input
              className="create__event"
              name="interest2"
              type="text"
              placeholder="What are you looking for?"
              value={this.state.activities} onChange={
                (e) => this.handleActivitiesUpdate(e)
                }
            />
            </p>
            <p>3 - <input
              className="create__event"
              name="interest3"
              type="text"
              placeholder="What are you looking for?"
              value={this.state.activities} onChange={
                (e) => {
                  this.setState({ activities: e.target.value })
                  e.preventDefault()
                }}
            />
            </p>
            <p>4 - <input
              className="create__event"
              name="interest4"
              type="text"
              placeholder="What are you looking for?"
              value={this.state.activities} onChange={
                (e) => {
                  this.setState({ activities: e.target.value })
                  e.preventDefault()
                }}
            />
            </p>
            <p>5 - <input
              className="create__event"
              name="interest5"
              type="text"
              placeholder="What are you looking for?"
              value={this.state.activities} onChange={
                (e) => {
                  this.setState({ activities: e.target.value })
                  e.preventDefault()
                }}
            />
            </p>
            <button type="submit"
              className="create__publish-event">Create</button>
          </form>
        </div>
      </>
    )
  }
}

export default MyInterests
